mapper.select:
select a.id id, a.user userId, a.title title, a.update_time time,a.description content,a.cover_image url, (select nums from cms_counts c where c.pid=a.id and c.type =0) likes, (select nums from cms_counts c where c.pid=a.id and c.type =1) collection, (select nums from cms_counts c where c.pid=a.id and c.type =2) countComment from cms_article a ,cms_community y
a.publish_type = #{param.type}
and y.id=a.parent_id and y.parent_id=#{param.gameId}
order by a.update_time desc

select a.id id, a.user userId, a.title title, a.update_time time,a.description content, (select nums from cms_counts c where c.pid=a.id and c.type =0) likes, (select nums from cms_counts c where c.pid=a.id and c.type =1) collection, (select nums from cms_counts c where c.pid=a.id and c.type =2) countComment from cms_article a,cms_community y
a.parent_id=y.id and y.parent_id=#{params.gameId}
and a.is_hot=#{params.isHot}
and a.publish_type=#{params.publishType}
and a.title like concat('%', #{params.title},'%')
order by a.sort desc, a.update_time desc
